window.downloadFile=function(e){if(/(iP)/g.test(navigator.userAgent)){alert("Your device does not support files downloading. Please try again in desktop browser.");return false}if(window.downloadFile.isChrome||window.downloadFile.isSafari){var t=document.createElement("a");t.href=e;if(t.download!==undefined){var n=e.substring(e.lastIndexOf("/")+1,e.length);t.download=n}if(document.createEvent){var r=document.createEvent("MouseEvents");r.initEvent("click",true,true);t.dispatchEvent(r);return true}}if(e.indexOf("?")===-1){e+="?download"}window.open(e,"_self");return true};window.downloadFile.isChrome=navigator.userAgent.toLowerCase().indexOf("chrome")>-1;window.downloadFile.isSafari=navigator.userAgent.toLowerCase().indexOf("safari")>-1